import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { itemsAPI } from '../services/api';

const LostItemForm = ({ token, user }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    category: '',
    item_name: '',
    brand: '',
    color: '',
    location: '',
    other_location: '',
    not_sure_location_description: '',
    date_lost: new Date().toISOString().split('T')[0],
    description: '',
    additional_info: '',
    images: []
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [submittedData, setSubmittedData] = useState(null);
  const [matchFound, setMatchFound] = useState(false);

  const categories = [
    'Electronics', 'Personal Items', 'Bags & Accessories', 
    'Books & Stationery', 'Clothing', 'Sports Equipment', 'Other'
  ];

  const colors = [
    { value: 'Black', emoji: 'âš«' },
    { value: 'White', emoji: 'âšª' },
    { value: 'Blue', emoji: 'ðŸ”µ' },
